INSERT INTO `mst_pages` VALUES(4, 'Privacy Policy', 'privacy_policy', '<p><strong style="color: rgb(75, 75, 75); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px;">PRIVACY</strong><span style="color: rgb(75, 75, 75); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px;">&nbsp;</span></p>\r\n<p style="color: rgb(75, 75, 75); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px;"><strong>This is the Privacy Policy for Twinkle Star Dance&trade;.</strong>&nbsp;<br />\r\n<strong>Our Contact Information is:</strong><br />\r\nTwinkle Star Dance&nbsp;<br />\r\n4046 East Avenue<br />\r\nLivermore, CA 94550<br />\r\n(925) 447-5299<br />\r\n<a href="mailto:support@twinklestardance.com" style="color: rgb(223, 63, 126);">support@twinklestardance.com</a>&nbsp;<br />\r\nWe collect no information about our visitor''s domain, but we do collect information about our visitor''s email address when available.<br />\r\n<strong>For visitors on our website we collect:</strong></p>\r\n<ul type="disc" style="list-style: circle; margin: 0px 0px 0px 15px; padding: 0px; color: rgb(75, 75, 75); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 18px;">\r\n    <li style="list-style: none; margin: 0px; padding: 0px; font-size: 12px; clear: both;">The email address and personal information from the visitors who communicate with us via email.</li>\r\n    <li style="list-style: none; margin: 0px; padding: 0px; font-size: 12px; clear: both;">Aggregate (General Tracking) information about pages that users visit on our site.</li>\r\n    <li style="list-style: none; margin: 0px; padding: 0px; font-size: 12px; clear: both;">User specific information about pages that users visit on our site.</li>\r\n    <li style="list-style: none; margin: 0px; padding: 0px; font-size: 12px; clear: both;">Any information given to us by users that contact us via inquiry form, survey, etc.</li>\r\n</ul>\r\n<p style="color: r