INSERT INTO `package_subscription` VALUES(7, 'Fire it UP! Adult Dance Fitness', '<h3><strong style="color: rgb(51, 51, 51); font-family: Georgia, ''Times New Roman'', Times, serif; line-height: 18px; ">Fire it Up Dance Fitness -&nbsp; <br />\r\na 50 minute jazz dance-based conditioning and fitness class.<br />\r\n</strong></h3>\r\n<p style="color: rgb(51, 51, 51); font-family: Georgia, ''Times New Roman'', Times, serif; line-height: 18px; ">Package includes 12 Fire it Up videos and new content quarterly.<br />\r\n<br />\r\nNo dance experience necessary for adults to experience the heat of dance fitness to music from multiple genres and decades&hellip;<br />\r\nLatin/Salsa<br />\r\nJitterbut/Swing<br />\r\nDisco<br />\r\n80&rsquo;s<br />\r\nHip-Hop/Funk<br />\r\nBroadway favorites<br />\r\nCountry and Western<br />\r\n&hellip;and of course the most current pop music!&nbsp;</p>\r\n<p style="color: rgb(51, 51, 51); font-family: Georgia, ''Times New Roman'', Times, serif; line-height: 18px; ">Cool the heat with an inspirational lyrical dance at the end of each class!</p>', 999, 49, NULL, 3, '661ba_20120918115021.jpg', 'Subscription', 15, 0, 1, '2012-09-18 11:50:21', '2012-11-06 14:04:42');
INSERT INTO `package_subscription` VALUES(8, '6 Twinkle Babies/Stars Choreography Videos', '<h3>6 ADDITIONAL RECITAL CHOREOGRAPHY VIDEOS</h3>\r\n<p>Build your repertoire! Select six additional recital choreography videos (streaming*).They''re yours to use for as long as you''re a subscriber. &nbsp;Note: only subscribers to Twinkle Star curriculum may subscribe to this package.<br />\r\n&nbsp;</p>\r\n<hr />\r\n<p><span style="font-family: arial, sans-serif; line-height: 22px; font-size: small; ">Costume Gallery<span style="color: rgb(255, 0, 0); ">&reg;</span>&nbsp;Perfectly Paired Recital Costumes<br />\r\n</span></p>\r\n<ul>\r\n    <li>\r\n    <p>Take the guesswork out of selecting costumes - we''ve partnered with Costume Gallery<span style="color: rgb(255, 0, 0); "><span style="font-family: Verdana, sans-serif; ">&reg;</span></span>&nbsp;&nbsp;to be our official costume supplier. &nbsp;We helped design and select at least one Costume Gallery<span style="color: rgb(255, 0, 0); "><span style="font-family: Verdana, sans-serif; ">&reg;</span></span>&nbsp;costume for each Twinkle Star Dance&trade; recital choreography video.</p>\r\n    </li>\r\n    <li>\r\n    <p>Bonus-use a free&nbsp;<a target="_blank" href="http://www.costumemanager.com/StudioOwnersandInstructorsGuide"><span style="color: rgb(0, 0, 0); ">CostumeManager.com account</span></a>&nbsp;to assign costumes to your dancers to purchase online or via telephone or order directly from Costume Gallery<span style="color: rgb(255, 0, 0); "><span style="font-family: Verdana, sans-serif; ">&reg;</span></span>. As a special perk for Twinkle Star Dance&trade; subscribers, CostumeManager.com offers the Costume Gallery recital costumes at catalog price!</p>\r\n    </li>\r\n</ul>\r\n<hr />\r\n<h3>&nbsp;Twinkle Babies&trade;&nbsp;2-3</h3>\r\n<ul>\r\n    <li>A Penny A Kiss&nbsp;- Ages 2-3 or 3-4 - Tap Movement&nbsp;</li>\r\n    <li>Baby Bumble Bee&nbsp;- Ages 2-3 - Ballet Movement</li>\r\n    <li>Bunny Rabbit Blues - Ages 2-3 - Ballet Movement</li>\r\n    <li><a href="http://youtu.be/WrfzgDNmPFM" target="_blank">Cuddle Bug Boogi</a><a href="http://youtu.be/WrfzgDNmPFM" target="_blank">e</a>&nbsp;- Ages 2-3 - Tap Movement</li>\r\n    <li>Little Clouds&nbsp;- Ages 2-3 - Ballet Movement&nbsp;</li>\r\n    <li>Little Tap Shoes&nbsp;- Ages 2-3 - Tap Movement&nbsp;</li>\r\n    <li>Me and My Teddy Bear&nbsp;2-3 - Ballet Movement</li>\r\n</ul>\r\n<hr />\r\n<h3>Twinkle Stars&trade; 3-4</h3>\r\n<ul>\r\n    <li>All Shook Up&nbsp;- Ages 3-4 - Tap Movement</li>\r\n    <li>Baby Take a Bow - Ages 3-4 - Tap Movement</li>\r\n    <li>Kimbo Bop&nbsp;- Ages 3-4 - Tap Movement&nbsp;</li>\r\n    <li>Magic Tutu&nbsp;- Ages 3-4 - Ballet Movement&nbsp;</li>\r\n    <li>Mickey Motion&nbsp;- Ages 3-4 - Tap Movement&nbsp;</li>\r\n    <li>Oh You Beautiful Doll&nbsp;- Ages 3-4 - Tap Movement&nbsp;</li>\r\n    <li>Oogie Boogie Woogie&nbsp;- Ages 3-4 - Tap Movement</li>\r\n    <li>Personality - Ages 3-4 - Tap Movement</li>\r\n    <li>Put on a Happy Face&nbsp;- Ages 3-4 - Tap Movement&nbsp;</li>\r\n    <li>Sound of Music&nbsp;- Ages 3-4 - Ballet Movement</li>\r\n    <li>Tiny Tots Ballet - Ages 3-4 - Ballet Movement&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n</ul>\r\n<hr />\r\n<h3>Twinkle Stars&trade; 3-4 or 4-5</h3>\r\n<ul>\r\n    <li>Dream Ballet&nbsp;- Ages 3-4 or 4-5 - Ballet Movement&nbsp;&nbsp;</li>\r\n    <li>Perfect Princess Tea&nbsp;- Ages 3-4 or 4-5 - Ballet Movement&nbsp;</li>\r\n    <li>Ready for the Show Tonight&nbsp;- Ages 3-4 or 4-5 - Tap Movement&nbsp;</li>\r\n    <li>Swingin on a Star&nbsp;- Ages 3-4 or 4-5 - Tap Movement&nbsp;</li>\r\n    <li>Twinkle Twinkle Little Star&nbsp;- Ages 3-4 or 4-5 - Ballet Movement</li>\r\n</ul>\r\n<hr />\r\n<h3>Twinkle Stars&trade; 4-5 or 5-6</h3>\r\n<ul>\r\n    <li>Blue Suede Shoes&nbsp;- Ages 4-5 or 5-6 - Tap Movement&nbsp;</li>\r\n    <li>Boots are Made for Walkin&nbsp;- Ages 4-5 or 5-6 - Jazz Movement</li>\r\n    <li><a href="http://youtu.be/FXoNH2ybh8Y" target="_blank">Broadway B